using System.Text.RegularExpressions;
using Package.Etermax.EterChallenge.Users.Domain.Errors;

namespace Package.Etermax.EterChallenge.Users.Domain
{
    public class EmailCredentials
    {
        private const string ValidEmailPattern = @"^(?!\.)(""([^""\r\\]|\\[""\r\\])*""|"
                                                 + @"([-a-z0-9!#$%&'*+/=?^_`{|}~]|(?<!\.)\.)*)(?<!\.)"
                                                 + @"@[a-z0-9][\w\.-]*[a-z0-9]\.[a-z][a-z\.]*[a-z]$";

        public readonly string Email;
        public readonly string Password;

        public EmailCredentials(string email, string password)
        {
            ValidateEmail(email);

            Email = email;
            Password = password;
        }

        private static void ValidateEmail(string email)
        {
            var validation = new Regex(ValidEmailPattern, RegexOptions.IgnoreCase);

            if (string.IsNullOrEmpty(email) || !validation.IsMatch(email))
            {
                throw new InvalidEmailException();
            }
        }
    }
}